import React, { useEffect } from "react";
import html2canvas from "html2canvas";
import jsPDF from "jspdf";

const DEBUG_MODE = false; 

const colors = {
  OffDuty: "#66cc66",
  Sleeper: "#9966ff",
  Driving: "#1E90FF",
  OnDuty: "#FFA500",
};

const statusY = {
  OffDuty: 162,     // top row
  Sleeper: 122,     // second row
  Driving: 84,      // third row
  OnDuty: 46,       // bottom row
};
function drawCanvas(ctx, dayActivities, dayIndex, bgImage, driverInfo) {
  const width = 1000;
  const height = (bgImage.naturalHeight / bgImage.naturalWidth) * width;

  ctx.clearRect(0, 0, width, height);
  ctx.drawImage(bgImage, 0, 0, width, height);

if (DEBUG_MODE) {
  ctx.strokeStyle = "red";
  ctx.lineWidth = 0.5;
  ctx.setLineDash([4, 3]); // dashed
  Object.entries(statusY).forEach(([status, yVal]) => {
    const yScaled = yVal * (height / bgImage.naturalHeight);
    ctx.beginPath();
    ctx.moveTo(0, yScaled);
    ctx.lineTo(width, yScaled);
    ctx.stroke();
    ctx.fillStyle = "red";
    ctx.font = "10px Arial";
    ctx.fillText(status, 10, yScaled - 2);
  });
  ctx.setLineDash([]);
}

  const scaleY = height / bgImage.naturalHeight;
  const scaleX = width / bgImage.naturalWidth;

  dayActivities.forEach((a) => {
    const start = new Date(a.start);
    const end = new Date(a.end);
    const startHours = start.getUTCHours() + start.getUTCMinutes() / 60;
    const endHours = end.getUTCHours() + end.getUTCMinutes() / 60;

    const xStart = (startHours / 24) * width;
    const xEnd = (endHours / 24) * width;
    const y = (statusY[a.status] || statusY.OffDuty) * scaleY;

    ctx.strokeStyle = colors[a.status] || "#000";
    ctx.lineWidth = 6;
    ctx.beginPath();
    ctx.moveTo(xStart, y);
    ctx.lineTo(xEnd, y);
    ctx.stroke();
  });

  ctx.fillStyle = "#000";
  ctx.font = "12px Arial";
  ctx.fillText(`Day ${dayIndex + 1}`, 920, 20);
}

const LogCanvas = ({ days, driverInfo, tripSummary }) => {
  

  useEffect(() => {
    if (!days) return;
    const bgImage = new Image();
    bgImage.src = "/blank-paper-log.png";

    days.forEach((day, idx) => {
      const canvas = document.getElementById(`log-${idx}`);
      if (!canvas) return;
      const ctx = canvas.getContext("2d");
      drawCanvas(ctx, day.activities, idx, bgImage, driverInfo);
    });
  }, [days, driverInfo]);

  if (!days || days.length === 0) return null;

  // helper to compute totals per provided activities array
  const calcTotalHours = (activities, status) =>
    activities
      .filter((a) => a.status === status)
      .reduce((sum, a) => {
        const s = new Date(a.start);
        const e = new Date(a.end);
        return sum + (e - s) / 1000 / 3600;
      }, 0);

  const handleDownloadPDF = async () => {
    const pdf = new jsPDF("l", "pt", "a4");
    const currentDate = new Date().toLocaleString();

    for (let i = 0; i < days.length; i++) {
      const canvas = document.getElementById(`log-${i}`);
      if (!canvas) continue;

      const canvasImage = await html2canvas(canvas, { scale: 2 });
      const imgData = canvasImage.toDataURL("image/png");
      const pageWidth = pdf.internal.pageSize.getWidth();
      const pageHeight = (canvas.height / canvas.width) * pageWidth;

      if (i > 0) pdf.addPage();

      // Header
      pdf.setFontSize(14);
      pdf.text("FMCSA DRIVER DAILY LOG SHEET", pageWidth / 2, 30, { align: "center" });
      pdf.setFontSize(10);
      pdf.text(`Driver: ${driverInfo?.name || ""}`, 20, 50);
      pdf.text(`Company: ${driverInfo?.company || ""}`, 20, 65);
      pdf.text(`Trip ID: ${driverInfo?.tripId || ""}`, 20, 80);
      pdf.text(`Date: ${days[i].date}`, 20, 95);

     
      pdf.addImage(imgData, "PNG", 0, 110, pageWidth, pageHeight);

      // Summaries below image
      const driveHours = calcTotalHours(days[i].activities, "Driving").toFixed(1);
      const dutyHours = calcTotalHours(days[i].activities, "OnDuty").toFixed(1);
      const restHours = calcTotalHours(days[i].activities, "OffDuty").toFixed(1);
      const summaryY = 110 + pageHeight + 20;

      pdf.setFontSize(10);
      pdf.text(`Driving Hours: ${driveHours} hrs`, 20, summaryY);
      pdf.text(`On Duty: ${dutyHours} hrs`, 240, summaryY);
      pdf.text(`Off Duty: ${restHours} hrs`, 460, summaryY);

      // Signature
      pdf.line(20, summaryY + 30, 220, summaryY + 30);
      pdf.text("Driver Signature", 20, summaryY + 45);

      // Timestamp
      pdf.setFontSize(8);
      pdf.text(`Generated: ${currentDate}`, pageWidth - 160, summaryY + 45);
    }

    pdf.save("FMCSA_Driver_Log.pdf");
  };

  return (
    <div style={{ marginTop: 20 }}>
      <h2>Driver Daily Log Sheets</h2>

      {days.map((d, idx) => (
        <div key={idx} style={{ marginTop: 16 }}>
          <div style={{ marginBottom: 8 }}>
            <strong>{d.date}</strong> — {d.activities.length} activities — fuel stops:{" "}
            {d.fuel_inserted ?? 0}
          </div>
        <canvas
  id={`log-${idx}`}
  width={1000}
  height={200}
  style={{
    display: "block",
    maxWidth: "100%",
    height: "auto",
    border: "1px solid #ddd",
    borderRadius: 6,
  }}
/>

        </div>
      ))}

      <div style={{ textAlign: "center", marginTop: 18 }}>
        <button onClick={handleDownloadPDF}>Download FMCSA PDF</button>
      </div>
    </div>
  );
};

export default LogCanvas;
