import React, { useState } from "react";
import { MapContainer, TileLayer, Marker, Polyline, Popup } from "react-leaflet";
import "leaflet/dist/leaflet.css";
import L from "leaflet";
import axios from "axios";

const MapComponent = () => {
  const [pickup, setPickup] = useState("Los Angeles, CA");
  const [dropoff, setDropoff] = useState("Dallas, TX");
  const [driverName, setDriverName] = useState("Jesse Kidula");

  const [trip, setTrip] = useState(null);
  const [stops, setStops] = useState([]);
  const [routeLine, setRouteLine] = useState([]);

  const [loading, setLoading] = useState(false);
  const [downloading, setDownloading] = useState(false);

  // Custom marker icons
  const fuelIcon = new L.Icon({
    iconUrl: "https://cdn-icons-png.flaticon.com/512/2857/2857391.png",
    iconSize: [30, 30],
  });

  const pickupIcon = new L.Icon({
    iconUrl: "https://cdn-icons-png.flaticon.com/512/684/684908.png",
    iconSize: [35, 35],
  });

  const dropoffIcon = new L.Icon({
    iconUrl: "https://cdn-icons-png.flaticon.com/512/1051/1051277.png",
    iconSize: [35, 35],
  });

  // Map for default coordinates
  const getCityCoords = (cityName) => {
    const coordsMap = {
      "Los Angeles, CA": [34.0522, -118.2437],
      "Dallas, TX": [32.7767, -96.7970],
      "New York, NY": [40.7128, -74.0060],
      "Chicago, IL": [41.8781, -87.6298],
      "Miami, FL": [25.7617, -80.1918],
      "Atlanta, GA": [33.749, -84.388],
      "Seattle, WA": [47.6062, -122.3321],
      "Denver, CO": [39.7392, -104.9903],
      "Houston, TX": [29.7604, -95.3698],
      "Las Vegas, NV": [36.1699, -115.1398],
    };
    return coordsMap[cityName] || [39.8283, -98.5795];
  };

  // Fetch trip plan
  const generateRoute = async () => {
    if (!pickup || !dropoff) {
      alert("Please enter both Pickup and Drop-off locations.");
      return;
    }

    try {
      setLoading(true);
      const res = await axios.post("http://127.0.0.1:8000/api/plan-route/", {
        origin: pickup,
        destination: dropoff,
        driver_name: driverName,
      });

      const data = res.data;
      setTrip(data.trip);
      setStops(data.stops);

      // build route path
      const points = [
        getCityCoords(data.trip.origin),
        ...data.stops.map((s) => [s.latitude, s.longitude]),
        getCityCoords(data.trip.destination),
      ];
      setRouteLine(points);
    } catch (err) {
      console.error("Plan request failed:", err);
      alert("‚ùå Could not generate trip. Check backend server or city inputs.");
    } finally {
      setLoading(false);
    }
  };

  // Download PDF from backend
  const downloadPDF = async () => {
    try {
      setDownloading(true);
      const res = await axios.get("http://127.0.0.1:8000/api/generate-log-pdf/", {
        responseType: "blob",
      });

      const url = window.URL.createObjectURL(new Blob([res.data]));
      const link = document.createElement("a");
      link.href = url;
      link.setAttribute("download", "daily_log_full.pdf");
      document.body.appendChild(link);
      link.click();
      link.remove();
      alert("‚úÖ Log PDF downloaded successfully!");
    } catch (err) {
      console.error("PDF download failed:", err);
      alert("‚ö†Ô∏è Failed to download the PDF. Try regenerating trip first.");
    } finally {
      setDownloading(false);
    }
  };

  return (
    <div
      className="map-wrapper"
      style={{
        height: "100vh",
        width: "100%",
        padding: "16px",
        display: "flex",
        flexDirection: "column",
      }}
    >
      <h1 style={{ fontSize: "22px", fontWeight: "bold", marginBottom: "10px" }}>
        üöõ ELD Route & Log Planner
      </h1>

      {/* Input Form */}
      <div
        style={{
          display: "flex",
          gap: "10px",
          alignItems: "center",
          flexWrap: "wrap",
          marginBottom: "10px",
        }}
      >
        <input
          type="text"
          placeholder="Pickup City, State"
          value={pickup}
          onChange={(e) => setPickup(e.target.value)}
          style={{
            padding: "8px",
            borderRadius: "6px",
            border: "1px solid #ccc",
            flex: "1",
          }}
        />
        <input
          type="text"
          placeholder="Dropoff City, State"
          value={dropoff}
          onChange={(e) => setDropoff(e.target.value)}
          style={{
            padding: "8px",
            borderRadius: "6px",
            border: "1px solid #ccc",
            flex: "1",
          }}
        />
        <input
          type="text"
          placeholder="Driver Name"
          value={driverName}
          onChange={(e) => setDriverName(e.target.value)}
          style={{
            padding: "8px",
            borderRadius: "6px",
            border: "1px solid #ccc",
            flex: "1",
          }}
        />
        <button
          onClick={generateRoute}
          disabled={loading}
          style={{
            backgroundColor: "#2563eb",
            color: "white",
            padding: "8px 16px",
            borderRadius: "8px",
            cursor: loading ? "not-allowed" : "pointer",
          }}
        >
          {loading ? "Generating..." : "üöö Generate Route"}
        </button>
        <button
          onClick={downloadPDF}
          disabled={downloading}
          style={{
            backgroundColor: "#16a34a",
            color: "white",
            padding: "8px 16px",
            borderRadius: "8px",
            cursor: downloading ? "not-allowed" : "pointer",
          }}
        >
          {downloading ? "Preparing PDF..." : "üìÑ Download Log PDF"}
        </button>
      </div>

      {/* Map Visualization */}
      <MapContainer
        center={[39.8283, -98.5795]}
        zoom={5}
        scrollWheelZoom={true}
        style={{
          height: "80vh",
          width: "100%",
          borderRadius: "10px",
          overflow: "hidden",
        }}
      >
        <TileLayer
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          attribution='&copy; <a href="https://www.openstreetmap.org/">OpenStreetMap</a> contributors'
        />

        {/* Origin Marker */}
        {trip && (
          <Marker position={getCityCoords(trip.origin)} icon={pickupIcon}>
            <Popup><b>Pickup:</b> {trip.origin}</Popup>
          </Marker>
        )}

        {/* Destination Marker */}
        {trip && (
          <Marker position={getCityCoords(trip.destination)} icon={dropoffIcon}>
            <Popup><b>Drop-off:</b> {trip.destination}</Popup>
          </Marker>
        )}

        {/* Fuel Stops */}
        {stops.map((stop, i) => (
          <Marker key={i} position={[stop.latitude, stop.longitude]} icon={fuelIcon}>
            <Popup>
              <b>Fuel Stop #{i + 1}</b><br />
              Lat: {stop.latitude.toFixed(2)}, Lon: {stop.longitude.toFixed(2)}<br />
              Time: {new Date(stop.timestamp).toLocaleString()}
            </Popup>
          </Marker>
        ))}

        {/* Route Polyline */}
        {routeLine.length > 1 && (
          <Polyline positions={routeLine} color="blue" weight={4} opacity={0.6} />
        )}
      </MapContainer>
    </div>
  );
};

export default MapComponent;
