import React, { useState, useEffect, useRef } from "react";
import { MapContainer, TileLayer, Marker, Polyline, Popup } from "react-leaflet";
import "leaflet/dist/leaflet.css";
import L from "leaflet";
import axios from "axios";

const colors = {
  OffDuty: "#66cc66",
  Sleeper: "#9966ff",
  Driving: "#1E90FF",
  OnDuty: "#FFA500",
};

const statusY = {
  OffDuty: 162,
  Sleeper: 122,
  Driving: 84,
  OnDuty: 46,
};

const fuelIcon = new L.Icon({
  iconUrl: "https://cdn-icons-png.flaticon.com/512/2857/2857391.png",
  iconSize: [30, 30],
});

const pickupIcon = new L.Icon({
  iconUrl: "https://cdn-icons-png.flaticon.com/512/684/684908.png",
  iconSize: [35, 35],
});

const dropoffIcon = new L.Icon({
  iconUrl: "https://cdn-icons-png.flaticon.com/512/1051/1051277.png",
  iconSize: [35, 35],
});

const getCityCoords = (cityName) => {
  const coordsMap = {
    "Los Angeles, CA": [34.0522, -118.2437],
    "Dallas, TX": [32.7767, -96.7970],
    "New York, NY": [40.7128, -74.0060],
    "Chicago, IL": [41.8781, -87.6298],
    "Miami, FL": [25.7617, -80.1918],
    "Atlanta, GA": [33.749, -84.388],
    "Seattle, WA": [47.6062, -122.3321],
    "Denver, CO": [39.7392, -104.9903],
    "Houston, TX": [29.7604, -95.3698],
    "Las Vegas, NV": [36.1699, -115.1398],
  };
  return coordsMap[cityName] || [39.8283, -98.5795];
};

// Draw a day's activities on canvas
function drawDayCanvas(ctx, day, bgImage) {
  const width = 1000;
  const height = (bgImage.naturalHeight / bgImage.naturalWidth) * width;
  ctx.clearRect(0, 0, width, height);
  ctx.drawImage(bgImage, 0, 0, width, height);

  const scaleY = height / bgImage.naturalHeight;

  day.activities.forEach((act) => {
    const start = new Date(act.start);
    const end = new Date(act.end);
    const startH = start.getUTCHours() + start.getUTCMinutes() / 60;
    const endH = end.getUTCHours() + end.getUTCMinutes() / 60;

    const xStart = (startH / 24) * width;
    const xEnd = (endH / 24) * width;
    const y = (statusY[act.status] || statusY.OffDuty) * scaleY;

    ctx.strokeStyle = colors[act.status] || "#000";
    ctx.lineWidth = 6;
    ctx.beginPath();
    ctx.moveTo(xStart, y);
    ctx.lineTo(xEnd, y);
    ctx.stroke();
  });

  ctx.font = "12px Arial";
  ctx.fillStyle = "#000";
  ctx.fillText(`Day: ${day.date}`, 850, 20);
}

const MapWithLogs = () => {
  const [pickup, setPickup] = useState("Los Angeles, CA");
  const [dropoff, setDropoff] = useState("Dallas, TX");
  const [driverName, setDriverName] = useState("Jesse Kidula");

  const [trip, setTrip] = useState(null);
  const [stops, setStops] = useState([]);
  const [routeLine, setRouteLine] = useState([]);
  const [logDays, setLogDays] = useState([]);

  const [loading, setLoading] = useState(false);
  const [downloading, setDownloading] = useState(false);

  const canvasRefs = useRef([]);

  const generateRoute = async () => {
    if (!pickup || !dropoff) return alert("Enter Pickup and Dropoff locations");

    try {
      setLoading(true);
      const res = await axios.post("http://127.0.0.1:8000/api/plan-route/", {
        origin: pickup,
        destination: dropoff,
        driver_name: driverName,
      });

      const data = res.data;
      setTrip(data.trip);
      setStops(data.stops);
      setRouteLine([
        getCityCoords(data.trip.origin),
        ...data.stops.map((s) => [s.latitude, s.longitude]),
        getCityCoords(data.trip.destination),
      ]);

      setLogDays(data.daily_logs || []); // backend should return daily logs
    } catch (err) {
      console.error(err);
      alert("Failed to generate route/logs.");
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    if (!logDays || logDays.length === 0) return;
    const bgImage = new Image();
    bgImage.src = "/blank-paper-log.png";

    bgImage.onload = () => {
      logDays.forEach((day, idx) => {
        const canvas = canvasRefs.current[idx];
        if (!canvas) return;
        const ctx = canvas.getContext("2d");
        drawDayCanvas(ctx, day, bgImage);
      });
    };
  }, [logDays]);

  const handleDownloadPDF = async () => {
    const jsPDF = (await import("jspdf")).jsPDF;
    const html2canvas = (await import("html2canvas")).default;

    const pdf = new jsPDF("l", "pt", "a4");
    const currentDate = new Date().toLocaleString();

    for (let i = 0; i < logDays.length; i++) {
      const canvas = canvasRefs.current[i];
      if (!canvas) continue;
      const canvasImage = await html2canvas(canvas, { scale: 2 });
      const imgData = canvasImage.toDataURL("image/png");
      const pageWidth = pdf.internal.pageSize.getWidth();
      const pageHeight = (canvas.height / canvas.width) * pageWidth;

      if (i > 0) pdf.addPage();
      pdf.setFontSize(14);
      pdf.text("FMCSA DRIVER DAILY LOG SHEET", pageWidth / 2, 30, { align: "center" });
      pdf.setFontSize(10);
      pdf.text(`Driver: ${driverName}`, 20, 50);
      pdf.text(`Trip ID: ${trip?.id || ""}`, 20, 65);
      pdf.text(`Date: ${logDays[i].date}`, 20, 80);

      pdf.addImage(imgData, "PNG", 0, 110, pageWidth, pageHeight);
      pdf.text(`Generated: ${currentDate}`, pageWidth - 160, 110 + pageHeight + 20);
    }

    pdf.save("FMCSA_Driver_Log.pdf");
  };

  return (
    <div style={{ display: "flex", flexDirection: "column", padding: 16 }}>
      <h1>ðŸš› ELD Route & Daily Logs</h1>

      <div style={{ display: "flex", gap: 10, flexWrap: "wrap", marginBottom: 10 }}>
        <input value={pickup} onChange={(e) => setPickup(e.target.value)} placeholder="Pickup City, State" style={{ flex: 1, padding: 8, borderRadius: 6, border: "1px solid #ccc" }} />
        <input value={dropoff} onChange={(e) => setDropoff(e.target.value)} placeholder="Dropoff City, State" style={{ flex: 1, padding: 8, borderRadius: 6, border: "1px solid #ccc" }} />
        <input value={driverName} onChange={(e) => setDriverName(e.target.value)} placeholder="Driver Name" style={{ flex: 1, padding: 8, borderRadius: 6, border: "1px solid #ccc" }} />
        <button onClick={generateRoute} disabled={loading} style={{ padding: "8px 16px", borderRadius: 8, backgroundColor: "#2563eb", color: "#fff" }}>
          {loading ? "Generating..." : "Generate Route"}
        </button>
        <button onClick={handleDownloadPDF} disabled={downloading} style={{ padding: "8px 16px", borderRadius: 8, backgroundColor: "#16a34a", color: "#fff" }}>
          Download PDF
        </button>
      </div>

      <MapContainer center={[39.8283, -98.5795]} zoom={5} scrollWheelZoom style={{ height: "400px", marginBottom: 16 }}>
        <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" attribution='&copy; OpenStreetMap contributors' />
        {trip && <Marker position={getCityCoords(trip.origin)} icon={pickupIcon}><Popup>{trip.origin}</Popup></Marker>}
        {trip && <Marker position={getCityCoords(trip.destination)} icon={dropoffIcon}><Popup>{trip.destination}</Popup></Marker>}
        {stops.map((s, i) => <Marker key={i} position={[s.latitude, s.longitude]} icon={fuelIcon}><Popup>Fuel Stop #{i + 1}<br />{new Date(s.timestamp).toLocaleString()}</Popup></Marker>)}
        {routeLine.length > 1 && <Polyline positions={routeLine} color="blue" weight={4} />}
      </MapContainer>

      {/* Daily Logs */}
      <h2>Daily Log Sheets</h2>
      {logDays.map((day, idx) => (
        <div key={idx} style={{ marginBottom: 16 }}>
          <canvas
            ref={(el) => (canvasRefs.current[idx] = el)}
            width={1000}
            height={200}
            style={{ width: "100%", border: "1px solid #ccc", borderRadius: 6 }}
          />
        </div>
      ))}
    </div>
  );
};

export default MapWithLogs;
